-- VHDL Configurator
-- 1-Bit Full Adder 

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
	Port ( <INPUT_BIT1> : in STD_LOGIC;
	<INPUT_BIT2> : in STD_LOGIC;
	--
	<CARRY_BIT> : out STD_LOGIC;
	<SUM_BIT> : out STD_LOGIC);
end <NAME>;

architecture Behavioral of <NAME> is

begin

	FULL_ADDER_PROC: process(<INPUT_BIT1>, <INPUT_BIT2>, <INPUT_CARRY_BIT>)
	begin
		if <INPUT_BIT1> = '0' then
			if <INPUT_BIT2> = '1' and <INPUT_CARRY_BIT> = '1' then
				<SUM_BIT> <= '0';
				<CARRY_BIT> <= '1';
			else
				if <INPUT_BIT2> = '0' and <INPUT_CARRY_BIT> = '0' then
					<SUM_BIT> <= '0';
				else
					<SUM_BIT> <= '1';
				end if;
				<CARRY_BIT> <= '0';
			end if;
		else
			if <INPUT_BIT2> = '1' or <INPUT_CARRY_BIT> = '1' then
				<CARRY_BIT> <= '1';
			else
			<CARRY_BIT> <= '0';
		end if;
		if (<INPUT_BIT2> = '1' and <INPUT_CARRY_BIT> = '1') or (<INPUT_BIT2> = '0' and <INPUT_CARRY_BIT> = '0') then
			<SUM_BIT> <= '1';
		else
			<SUM_BIT> <= '0';
		end if;
	end if;
	end process;

end Behavioral;
