-- VHDL Configurator
-- 1-Bit Half Adder
-- By: Marcelo Machado

use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
port (
	<INPUT_BIT1>, <INPUT_BIT2> : in std_logic;
	<SUM_BIT>, <CARRY_BIT> : out std_logic
);
end <NAME>;

architecture Behavioral of <NAME> is
begin
	<CARRY_BIT> <= <INPUT_BIT1> AND <INPUT_BIT2>;
	<SUM_BIT> <= <INPUT_BIT1> XOR <INPUT_BIT2>;
end Behavioral;
