--Non-exact timer with 50% duty cycle
library IEEE;
use IEEE.STD_LOGIC_1164.ALL;
use IEEE.STD_LOGIC_UNSIGNED.ALL;
use IEEE.math_real.all;
entity <NAME> is
    Port ( <RESET> : in  STD_LOGIC;
           <CLOCK> : in  STD_LOGIC;
           <OUTPUT> : out  STD_LOGIC);
end <NAME>;
architecture Behavioral of <NAME> is
    Constant pulses: integer:=<FREQ_IN>/<FREQ_OUT>;
    constant limit: integer:= pulses-1;
    Constant bits: integer:= integer(ceil(log2(real(pulses))));
    signal counter: STD_LOGIC_VECTOR(bits-1 downto 0);
begin
    process(<RESET>,<CLOCK>)
    begin
        if <RESET> = '1' then
            counter<= (others =>'0');
        elsif <EDGE_TYPE>(<CLOCK>) then 
            counter <= counter + 1;
        end if;
    end process;
    <OUTPUT> <= counter(bits-1);
end Behavioral;
