--Register of N-bits
--Made by Carmen Rodr√≠guez Villaverde

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;
entity <NAME> is
    Generic (N: integer);
    Port ( <LOAD> : in  STD_LOGIC;
           <CLOCK> : in  STD_LOGIC;
           <RESET> : in  STD_LOGIC;
           <INPUT> : in  STD_LOGIC_VECTOR (N-1 downto 0);
           --
           <OUTPUT> : out  STD_LOGIC_VECTOR (N-1 downto 0));
end <NAME>;
architecture Behavioral of <NAME> is
begin
    process(<CLOCK>,<RESET>)
        begin
            if <RESET>='1' then
                <OUTPUT><=(others=>'0');
            elsif <EDGE_TYPE>(<CLOCK>) then
                if <LOAD>='1' then
                    <OUTPUT><=<INPUT>;
                end if;
            end if;
    end process;
end Behavioral;
