-- VHDL Configurator
-- PIPO Shift Register 

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
	Port ( <CLOCK> : in STD_LOGIC;
	<RESET> : in STD_LOGIC;
	<INPUT> : in STD_LOGIC;
	--
	<OUTPUT> : out STD_LOGIC_VECTOR (<NUMBER_OF_BITS_MINUS_1> downto 0));
end <NAME>;

architecture Behavioral of <NAME> is 

signal Q: STD_LOGIC_VECTOR(<NUMBER_OF_BITS_MINUS_1> downto 0);

begin

	PIPO_REGISTER: process(<Clock>)
	begin
		if <EDGE_TYPE>(<CLOCK>) then
			if <RESET> = '1' then
				Q_signal <= (others => '0');
			else
				if LOAD = '1' then
					Q_signal((<NUMBER_OF_BITS_MINUS_1>) downto (<MIDDLE_BIT_MINUS_1>)) <= Q_signal((<MIDDLE_BIT>) downto 0);
				else
					Q_signal(<NUMBER_OF_BITS_MINUS_1>) <= <INPUT>(<NUMBER_OF_BITS_MINUS_1>);
					<AUTO_GENERATE>
				end if;
				Q_signal(0) <= <INPUT>(0);
			end if;
		end if;
	end process;

	<OUTPUT> <= Q_signal;
end Behavioral;

