moduleCode:
-- This code converts a N-bit binary number into its reflected binary code form (RBC aka Gray Code)

library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
    Port ( <INPUT1> : in  STD_LOGIC_VECTOR (<NUMBER_OF_BITS> downto 0);
           <OUTPUT> : out  STD_LOGIC_VECTOR ((<NUMBER_OF_BITS> downto 0));
end <NAME>

architecture Behavioral of <NAME> is

begin
	process(<INPUT1>)
	begin
		for i in 0 to (<NUMBER_OF_BITS>-1) loop
			<OUTPUT>(i)<= <INPUT1>(i) xor <INPUT1>(i+1);
		end loop;
		<OUTPUT>(3)<= <INPUT1>(<NUMBER_OF_BITS>);	
	end process;
end Behavioral;
