library IEEE;
use IEEE.STD_LOGIC_1164.ALL;

entity <NAME> is
	Port ( D : in STD_LOGIC;
	clk : in STD_LOGIC;
	Q : out STD_LOGIC);
end <NAME>;

architecture Behavioral of <NAME> is

begin
	process(clk)
	begin
		if(<EDGE_TYPE>(clk)) then
			Q <= D;
		end if;
	end process;
end Behavioral;
